export default {
  name: 'JInputPop',
  props: {
    title: {
      type: String,
      default: '',
      required: false
    },
    position: {
      type: String,
      default: 'right',
      required: false
    },
    height: {
      type: Number,
      default: 200,
      required: false
    },
    width: {
      type: Number,
      default: 150,
      required: false
    },
    value: {
      type: String,
      required: false
    },
    popContainer: {
      type: String,
      default: '',
      required: false
    },
    disabled: {
      type: Boolean,
      default: false
    }
  },
  data: function data() {
    return {
      visible: false,
      inputContent: ''
    };
  },
  watch: {
    value: {
      immediate: true,
      handler: function handler() {
        if (this.value && this.value.length > 0) {
          this.inputContent = this.value;
        }
      }
    }
  },
  model: {
    prop: 'value',
    event: 'change'
  },
  methods: {
    handleInputChange: function handleInputChange(event) {
      this.inputContent = event.target.value;
      this.$emit('change', this.inputContent);
    },
    pop: function pop() {
      this.visible = true;
    },
    getPopupContainer: function getPopupContainer(node) {
      if (!this.popContainer) {
        return node.parentNode;
      } else {
        return document.getElementById(this.popContainer);
      }
    }
  }
};
