import JSelectDepartModal from './modal/JSelectDepartModal';
export default {
  name: 'JSelectDepart',
  components: {
    JSelectDepartModal: JSelectDepartModal
  },
  props: {
    modalWidth: {
      type: Number,
      default: 500,
      required: false
    },
    multi: {
      type: Boolean,
      default: false,
      required: false
    },
    rootOpened: {
      type: Boolean,
      default: true,
      required: false
    },
    value: {
      type: String,
      required: false
    },
    disabled: {
      type: Boolean,
      required: false,
      default: false
    },
    // 自定义返回字段，默认返回 id
    customReturnField: {
      type: String,
      default: 'id'
    }
  },
  data: function data() {
    return {
      visible: false,
      confirmLoading: false,
      departNames: "",
      departIds: ''
    };
  },
  mounted: function mounted() {
    this.departIds = this.value;
  },
  watch: {
    value: function value(val) {
      if (this.customReturnField === 'id') {
        this.departIds = val;
      }
    }
  },
  methods: {
    initComp: function initComp(departNames) {
      var _this = this;

      this.departNames = departNames; //update-begin-author:lvdandan date:20200513 for:TESTA-438 部门选择组件自定义返回值，数据无法回填
      //TODO 当返回字段为部门名称时会有问题,因为部门名称不唯一
      //返回字段不为id时，根据返回字段获取id

      if (this.customReturnField !== 'id' && this.value) {
        var dataList = this.$refs.innerDepartSelectModal.dataList;
        console.log('this.value', this.value);
        this.departIds = this.value.split(',').map(function (item) {
          var data = dataList.filter(function (d) {
            return d[_this.customReturnField] === item;
          });
          return data.length > 0 ? data[0].id : '';
        }).join(',');
      } //update-end-author:lvdandan date:20200513 for:TESTA-438 部门选择组件自定义返回值，数据无法回填

    },
    openModal: function openModal() {
      this.$refs.innerDepartSelectModal.show();
    },
    handleOK: function handleOK(rows, idstr) {
      var _this2 = this;

      var value = '';

      if (!rows && rows.length <= 0) {
        this.departNames = '';
        this.departIds = '';
      } else {
        value = rows.map(function (row) {
          return row[_this2.customReturnField];
        }).join(',');
        this.departNames = rows.map(function (row) {
          return row['departName'];
        }).join(',');
        this.departIds = idstr;
      }

      this.$emit("change", value);
    },
    getDepartNames: function getDepartNames() {
      return this.departNames;
    },
    handleEmpty: function handleEmpty() {
      this.handleOK('');
    }
  },
  model: {
    prop: 'value',
    event: 'change'
  }
};
