var JINPUT_QUERY_LIKE = 'like';
var JINPUT_QUERY_NE = 'ne';
var JINPUT_QUERY_GE = 'ge'; //大于等于

var JINPUT_QUERY_LE = 'le'; //小于等于

export default {
  name: 'JInput',
  props: {
    value: {
      type: String,
      required: false
    },
    type: {
      type: String,
      required: false,
      default: JINPUT_QUERY_LIKE
    },
    placeholder: {
      type: String,
      required: false,
      default: ''
    }
  },
  watch: {
    value: {
      immediate: true,
      handler: function handler() {
        this.initVal();
      }
    },
    // update-begin author:sunjianlei date:20200225 for:当 type 变化的时候重新计算值 ------
    type: function type() {
      this.backValue({
        target: {
          value: this.inputVal
        }
      });
    } // update-end author:sunjianlei date:20200225 for:当 type 变化的时候重新计算值 ------

  },
  model: {
    prop: 'value',
    event: 'change'
  },
  data: function data() {
    return {
      inputVal: ''
    };
  },
  methods: {
    initVal: function initVal() {
      if (!this.value) {
        this.inputVal = '';
      } else {
        var text = this.value;

        switch (this.type) {
          case JINPUT_QUERY_LIKE:
            text = text.substring(1, text.length - 1);
            break;

          case JINPUT_QUERY_NE:
            text = text.substring(1);
            break;

          case JINPUT_QUERY_GE:
            text = text.substring(2);
            break;

          case JINPUT_QUERY_LE:
            text = text.substring(2);
            break;

          default:
        }

        this.inputVal = text;
      }
    },
    backValue: function backValue(e) {
      var text = e.target.value;

      switch (this.type) {
        case JINPUT_QUERY_LIKE:
          text = "*" + text + "*";
          break;

        case JINPUT_QUERY_NE:
          text = "!" + text;
          break;

        case JINPUT_QUERY_GE:
          text = ">=" + text;
          break;

        case JINPUT_QUERY_LE:
          text = "<=" + text;
          break;

        default:
      }

      this.$emit("change", text);
    }
  }
};
