import { ajaxGetDictItems, getDictItemsFromCache } from '@/api/api';
export default {
  name: "JDictSelectTag",
  props: {
    dictCode: String,
    placeholder: String,
    triggerChange: Boolean,
    disabled: Boolean,
    value: [String, Number],
    type: String,
    getPopupContainer: {
      type: Function,
      default: function _default(node) {
        return node.parentNode;
      }
    }
  },
  data: function data() {
    return {
      dictOptions: [],
      tagType: ""
    };
  },
  watch: {
    dictCode: {
      immediate: true,
      handler: function handler() {
        this.initDictData();
      }
    }
  },
  created: function created() {
    // console.log(this.dictCode);
    if (!this.type || this.type === "list") {
      this.tagType = "select";
    } else {
      this.tagType = this.type;
    } //获取字典数据
    // this.initDictData();

  },
  computed: {
    getValueSting: function getValueSting() {
      // update-begin author:wangshuai date:20200601 for: 不显示placeholder的文字 ------
      // 当有null或“” placeholder不显示
      return this.value != null ? this.value.toString() : undefined; // update-end author:wangshuai date:20200601 for: 不显示placeholder的文字 ------
    }
  },
  methods: {
    initDictData: function initDictData() {
      var _this = this;

      //优先从缓存中读取字典配置
      if (getDictItemsFromCache(this.dictCode)) {
        this.dictOptions = getDictItemsFromCache(this.dictCode);
        return;
      } //根据字典Code, 初始化字典数组


      ajaxGetDictItems(this.dictCode, null).then(function (res) {
        if (res.success) {
          //                console.log(res.result);
          _this.dictOptions = res.result;
        }
      });
    },
    handleInput: function handleInput(e) {
      var val;

      if (this.tagType == "radio") {
        val = e.target.value;
      } else {
        val = e;
      }

      console.log(val);

      if (this.triggerChange) {
        this.$emit('change', val);
      } else {
        this.$emit('input', val);
      }
    },
    setCurrentDictOptions: function setCurrentDictOptions(dictOptions) {
      this.dictOptions = dictOptions;
    },
    getCurrentDictOptions: function getCurrentDictOptions() {
      return this.dictOptions;
    }
  }
};
