import pick from 'lodash.pick'
import { FormTypes,getRefPromise } from '@/utils/JEditableTableUtil'
import { JEditableTableMixin } from '@/mixins/JEditableTableMixin'
import { validateDuplicateValue } from '@/utils/util'

export default {
  name: 'PipeInfoModal',
  mixins: [JEditableTableMixin],
  components: {
  },
  data() {
    return {
      labelCol: {
        xs: { span: 24 },
        sm: { span: 6 },
      },
      wrapperCol: {
        xs: { span: 24 },
        sm: { span: 16 },
      },
      labelCol2: {
        xs: { span: 24 },
        sm: { span: 3 },
      },
      wrapperCol2: {
        xs: { span: 24 },
        sm: { span: 20 },
      },
      // 新增时子表默认添加几行空数据
      addDefaultRowNum: 1,
      validatorRules: {
      },
      refKeys: ['sensorLocInfo', ],
      tableKeys:['sensorLocInfo', ],
      activeKey: 'sensorLocInfo',
      // 节点位置信息
      sensorLocInfoTable: {
        loading: false,
        dataSource: [],
        columns: [
          {
            title: '设备号',
            key: 'uid',
            type: FormTypes.input,
            width:"200px",
            placeholder: '请输入${title}',
            defaultValue: '',
          },
          {
            title: '创建人',
            key: 'createBy',
            type: FormTypes.input,
            width:"200px",
            placeholder: '请输入${title}',
            defaultValue: '',
          },
          {
            title: '纬度',
            key: 'latitude',
            type: FormTypes.input,
            width:"200px",
            placeholder: '请输入${title}',
            defaultValue: '',
          },
          {
            title: '经度',
            key: 'longitude',
            type: FormTypes.input,
            width:"200px",
            placeholder: '请输入${title}',
            defaultValue: '',
          },
          // {
          //   title: '所属管道',
          //   key: 'pipeId',
          //   type: FormTypes.input,
          //   width:"200px",
          //   placeholder: '请输入${title}',
          //   defaultValue: '',
          // },
          {
            title: '节点描述',
            key: 'desp',
            type: FormTypes.input,
            width:"200px",
            placeholder: '请输入${title}',
            defaultValue: '',
          },
        ]
      },
      url: {
        add: "/pipeinfo/pipeInfo/add",
        edit: "/pipeinfo/pipeInfo/edit",
        sensorLocInfo: {
          list: '/pipeinfo/pipeInfo/querySensorLocInfoByMainId'
        },
      }
    }
  },
  methods: {
    getAllTable() {
      let values = this.tableKeys.map(key => getRefPromise(this, key))
      return Promise.all(values)
    },
    /** 调用完edit()方法之后会自动调用此方法 */
    editAfter() {
      console.log(this.model)
      let fieldval = pick(this.model,'pipeId','pipeDes','hlat','hlong','rlat','rlong')
      this.$nextTick(() => {
        this.form.setFieldsValue(fieldval)
      })
      // 加载子表数据
      if (this.model.id) {
        let params = { id: this.model.id }
        this.requestSubTableData(this.url.sensorLocInfo.list, params, this.sensorLocInfoTable)
      }
    },
    /** 整理成formData */
    classifyIntoFormData(allValues) {
      let main = Object.assign(this.model, allValues.formValue)
      return {
        ...main, // 展开
        sensorLocInfoList: allValues.tablesValue[0].values,
      }
    },
    validateError(msg){
      this.$message.error(msg)
    },
   popupCallback(row){
     this.form.setFieldsValue(pick(row,'pipeId','pipeDes','hlat','hlong','rlat','rlong'))
   },

  }
}
